import React from "react";
import cold from "./cold.png";
import hot from "./hot.png";

const images = [
    cold,
    hot,
]

const testTemp = 34;
const imageSource = "";

function whichImage() {
    if (testTemp > 30) {
        imageSource = images[1]
    } else {
        imageSource = images[0]
    };
    return imageSource;
};



const Result = () => (
    <div>
        <br></br> 
        <br></br>   
        <img src={whichImage()} height="300" width="500"></img>
        <br></br>
        <p>Insert water saved here</p>
    </div>
);


export default Result;
