    imageSource = images[0];
    while (testTemp > 30) {
        function myFunction() { 
            setInterval(myTimer, 1000);
        }
        myFunction();
        function myTimer() {
            waterSaved += flowRate;
        };
        imageSource = images[1];
        return imageSource;
    };
    return imageSource
};

const chooseImage = whichImage();

